import './CSS/freewrite.css'

const updateFreeWrite = (e, setFreeWriteState) => {
    setFreeWriteState(e)
}

export const freeWrite = (freeWriteRef, freeWriteState, setFreeWriteState, freeWriteModeFlag, setFreeWriteModeFlag) => {




    return (
        <div>
            <div className="freeWrite">
                <input ref={freeWriteRef} className='freewriteInput' value={freeWriteState}></input>
                <button>自由記述<br/>編集</button>
                <input
                    className='freewriteInput'
                    id={freeWriteState}
                    onChange={(e) => {updateFreeWrite(e.target.value, setFreeWriteState); console.log(e)}}>
                </input>
            </div>
        </div>

    )
}
