// import ""

const pushPlus = (addScoreState, setAddScoreState) => {
  setAddScoreState(addScoreState + 1)
}

const pushMinus = (addScoreState, setAddScoreState) => {
  setAddScoreState(addScoreState - 1)
}

export const BaseballButton = (addScoreState, setAddScoreState) => {



  return (
    <div>
      <button>得点:{addScoreState}</button>
      <i className="fa-light fa-square-plus" onClick={() => pushPlus(addScoreState, setAddScoreState)}></i>
      <i className="fa-light fa-square-minus" onClick={() => pushMinus(addScoreState, setAddScoreState)}></i>
      <i class="fa-solid fa-face-smile"></i>
      
    </div>
  )
}

export default BaseballButton
