import React, { useState, useEffect, useRef } from "react"
import { myIndexOf } from "./functions/myIndexOf";
const { Schools } = require("../../../../DB/Schools"); //分割代入
const { Position } = require("../../../../DB/Position")
const { Member } = require("../../../../DB/Member")


export const InputMember = () => {
    const ining = 1;
    const number = 1;

    const iningRef = useRef(null)
    const schoolRef = useRef(null)
    const positionRef = useRef(null)
    const numberRef = useRef(null)
    const nameKanjiRef = useRef(null)
    const nameHiraRef = useRef(null)

    const [iningState, setIningState] = useState(ining)
    const [schoolState, setSchoolState] = useState(1)
    const [positionState, setPositionState] = useState(1)
    const [numberState, setNumberState] = useState(1)

    const [handedHitState, setHandedHitState] = useState(true)
    const [handedThrowState, setHandedThrowState] = useState(true)

    //メンバーを選択しているかどうかのフラグをつくる
    let copyArray = [false]
    for (let i = 0; i < Member.length - 1; i++) {
        copyArray = [...copyArray, false]
    }
    const [selectedMember, setSelectedMember] = useState(copyArray)

    //クリック時メンバー選択
    const handleSelected = (ind) => {
        selectedMember[ind] = !selectedMember[ind]
        setSelectedMember(selectedMember)
        console.log(selectedMember)
    }

    const [copyMember, setCopyMember] = useState(Member)

    const initialSetIning = () => {
        for (let i = 1; i <= 3; i++) {
            const option = document.createElement('option')
            option.value = i
            option.text = i
            iningRef.current.appendChild(option)
        }
        console.log(Member[0].player_name_kanji)
    }

    const initialSetNumber = () => {
        for (let i = 1; i <= 50; i++) {
            const option = document.createElement('option')
            option.value = i;
            option.text = i;
            numberRef.current.appendChild(option)
        }
    }

    const initialSetPosition = () => {
        for (let i = 1; i <= 9; i++) {
            const option = document.createElement('option')
            option.value = Position[i - 1].kata
            option.text = Position[i - 1].kata
            positionRef.current.appendChild(option)
        }
    }

    const initialSetSchool = () => {
        for (let i = 1; i <= Schools.length; i++) {
            const option = document.createElement('option')
            option.value = Schools[i - 1].school
            option.text = Schools[i - 1].school
            schoolRef.current.appendChild(option)
        }
    }


    useEffect(() => {
        initialSetIning();
        initialSetNumber();
        initialSetPosition()
        initialSetSchool();
    }, [])




    const selectIning = (e) => {
        setIningState(e.target.value)
    }
    const selectNumber = (e) => {
        setNumberState(e.target.value)
    }
    const selectPosition = (e) => {
        setPositionState(e.target.value)
    }
    const selectSchool = (e) => {
        setSchoolState(e.target.value)
    }

    const handleHandedHit = () => {
        setHandedHitState(!handedHitState)
    }
    const handleHandedThrow = () => {
        setHandedThrowState(!handedThrowState)
    }


    const handleSousin = () => {
        fetch("http://localhost:5000/member/member_register", {
            method: "POST",
            mode: "cors",
            headers: {
                "Content-Type": "application/json",
            },
            //body: JSON.stringify({ email:login_id , password:login_ps }),
            body: JSON.stringify(Member),
        })
    }

    const handleMembers = () => {

        setCopyMember([{
            "game_id": 1,
            "school_id": 1,
            "player_name_kanji": nameKanjiRef.current.value,
            "player_name_hira": nameHiraRef.current.value,
            "position": Position[myIndexOf(Position, "kata", positionRef.current.value)].eng,
            "uniform_number": numberRef.current.value,
            "grade": iningRef.current.value,
            "handed_hit": "右",
            "handed_throw": "右"
        }, ...copyMember])
        console.log(copyMember)
    }


    return (
        <>
            <h1>選手登録画面{<button onClick={handleSousin}>送信</button>}</h1>

            <div className="toroku">
                <div className="MakeGame">
                    <select ref={schoolRef} value={schoolState} onChange={selectSchool} ></select>
                    <br />
                    学年<select ref={iningRef} value={iningState} onChange={selectIning} ></select>&nbsp;
                    位置<select ref={positionRef} value={positionState} onChange={selectPosition} ></select>&nbsp;
                    背番号<select ref={numberRef} value={numberState} onChange={selectNumber} ></select>&nbsp;&nbsp;

                    {handedHitState && <button style={{ color: "white", background: "mediumblue" }}>右打</button>}
                    {handedHitState && <button style={{ color: "white", background: "lightskyblue" }} onClick={handleHandedHit}>左打</button>}
                    {!handedHitState && <button style={{ color: "white", background: "lightskyblue" }} onClick={handleHandedHit}>右打</button>}
                    {!handedHitState && <button style={{ color: "white", background: "mediumblue" }}>左打</button>}<br />

                    名前（漢字）　<input ref={nameKanjiRef} ></input>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;

                    {handedThrowState && <button style={{ color: "white", background: "mediumblue" }}>右投</button>}
                    {handedThrowState && <button style={{ color: "white", background: "lightskyblue" }} onClick={handleHandedThrow}>左投</button>}
                    {!handedThrowState && <button style={{ color: "white", background: "lightskyblue" }} onClick={handleHandedThrow}>右投</button>}
                    {!handedThrowState && <button style={{ color: "white", background: "mediumblue" }}>左投</button>}<br />

                    名前（ひらがな）　<input ref={nameHiraRef}></input> &nbsp; &nbsp; <button onClick={handleMembers}>追加</button>
                </div>
                <hr></hr>
            </div>
            <div className="hyoji">
                <div className="players">
                    {copyMember.map((member, ind) => (
                        <div className="school">
                            <button onClick={() => handleSelected(ind)}>
                                {member.player_name_hira} &nbsp; {member.grade}年 &nbsp; {member.position}
                                &nbsp; 背番号{member.uniform_number} &nbsp; {member.handed_hit}打 &nbsp; {member.handed_throw}投
                                <hr></hr>{member.player_name_kanji}
                            </button><br /><br />
                        </div>
                    ))}
                </div>
            </div>


        </>
    )
}

export default InputMember
