//const { Score } = require("../../../../../DB/Score")

export const scoreBoard = (scoreState, nowIningState) => {
    console.log(scoreState)

    let teamAscore = 0
    let teamBscore = 0



    return (
        <div>
            <table className="scoreBoardTable" border="1">
                <tr className="scoreBoardTr" >
                    <th className="scoreBoardSchoolName"></th>
                    {scoreState[0].map((a, ind) =>
                        <th>{ind + 1}</th>
                    )}
                    <th></th>
                </tr>
                <tr>
                    <td>今治西</td>
                    {scoreState[0].map((a, ind) => {
                        teamAscore = a;
                        return (<td>{a}</td>)
                    }

                    )}
                    <td>{teamAscore}</td>
                </tr>
                <tr>
                    <td>西条</td>
                    {scoreState[1].map((a, ind) => {
                        teamBscore = a;
                        return (<td>{a}</td>)
                    }

                    )}
                    <td>{teamBscore}</td>
                </tr>
            </table>
        </div>

    )
}
