import React from "react";

export const Hyoji = (UseSchools, navigate, urlTournamentName, urlTournamentId, isEditMode, EditSchoolPopup) => {

    const PageTransition = (url) => {
        navigate(url)
    }

    return (
        <div className="main">
            <div className="Schools">
                {UseSchools.map(school => {
                    if (school.school_name !== null) {
                        if (school.IsCheck) {
                            return (
                                <div className="school">
                                    {!isEditMode &&
                                        <>
                                            <br />
                                            <button
                                                onClick={() => {
                                                    PageTransition(school.school_name + "/InputMember" +
                                                        "?urlTournamentId=" + urlTournamentId + "&urlTournamentName=" + urlTournamentName +
                                                        "&urlSchoolId=" + school.school_id + "&urlSchoolName=" + school.school_name);
                                                }} className="schoolBtn">
                                                {school.school_name}
                                            </button>
                                            <br />
                                        </>
                                    }
                                    {isEditMode &&
                                        <>
                                            <EditSchoolPopup
                                                school={school}
                                                EorDcheckBox={this.props.EorDcheckBox}
                                                setEorDcheckBox={this.props.setEorDcheckBox}
                                                editSchool={this.props.EditSchool}
                                                DeleteSchool={this.props.DeleteSchool}
                                                readSchool={this.props.readSchool}
                                                setUseSchools={this.props.setUseSchools}
                                                urlTournamentId={this.props.urlTournamentId}
                                            />
                                            />
                                        </>
                                    }

                                </div>
                            )
                        }
                    }
                })
                }
            </div>
        </div>
    )

}

export default Hyoji;
