import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom'
import { OptionButton } from '../optionFunc/OptionButton'
import { TitleBar } from "../TitleBar/TitleBar";
import EditVenuePopup from "./com/EditVenuePopup/EditVenuePopup";


//バックエンドのurlを取得
const backendUrl = require("../../../DB/communication").backendUrl;


//送られた文字列がどれか空ならtrue
const isEnpty = (strArray) => {
  let flag = false
  strArray.map((str) => {
    if (!str) {
      flag = true
    }
  })
  return flag
}

//送る文字列が被っていればtrue
const isDuplicate = (Array, key, id) => {
  let flag = false
  Array.map((u) => {
    if (u[id] === key) {
      flag = true
    }
  })
  console.log(key)
  return flag
}

//大会を読み込む
const readTournament = (setTournamentData) => {
  fetch(backendUrl + "/tournament/tournament_call", {
    method: "POST",
    mode: "cors",
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => response.json())
    .then((data) => setTournamentData(data))
}


//文字分割
const dateSplit = (nowdate) => {
  if (nowdate !== null) {
    let dateArray = nowdate.split('-');
    dateArray = { "year": dateArray[0], "month": dateArray[1], "day": dateArray[2] }
    return dateArray
  }

}





//自作プルダウン
const makePulldown = (pulldownId, ArrayList, idText, nowSelected, setNowSelected) => {
  //pulldownIdは0でいいです。
  //ArrayListは表示したい要素を並べた配列です、普通の配列ではなく連想配列です。
  //idテキストは連想配列の属性を書きます。
  //nowSelectedは今プルダウンで何が選択されているかが入ります。初期値は[0]で、これは0番目の値が選択されている状態です。
  //setNowSelectedはnowSelecedの値をuseStateの機能で上書きする関数です。setNowSelected(更新値)とすれば、nowSelectedに更新値が入ります。

  return (
    <>
      <select id="tekitouni"
        onChange={(e) => {
          //ステイトが変化すると再描画させるための文、これがないと再描画されない
          //なお、消すと再描画はされないが内部は変化する
          nowSelected = nowSelected.slice(0, nowSelected.length);
          nowSelected[pulldownId] = e.target.value
          setNowSelected(nowSelected)
          console.log(nowSelected)
        }
        }>
        {ArrayList.map((component, ind) => (
          <option value={ind}>{component[idText]}</option>
        ))
        }
      </select>
    </>
  )
}



//追加ボタン押したとき
const addVenue = (venueArray,setVenueArray, nowVenueName,) => {

  //被りチェック
  if (venueArray.some((v) => v.venue_name === nowVenueName)) {
    console.log("名前被ってます")
  }
  else if (nowVenueName === "") {
    console.log("空白です")
  }
  else {
    console.log(nowVenueName + "を登録します")

    fetch(backendUrl + "/venue/venue_register", {
      method: "POST", mode: "cors", headers: { "Content-Type": "application/json", },
      body: JSON.stringify({ "venue_name": nowVenueName }),
    })
      .then(() => loadVenue(setVenueArray))
  }
}

//大会の情報を修正する
const editTournament = (urlTournamentId, newTournamentName, openingDate, TournamentData, setTournamentData) => {
  if (TournamentData.some((v) => v.tournament_name === newTournamentName)) {
    console.log("名前被ってます")
  }
  else {
    fetch(backendUrl + "/tournament/tournament_edit", {
      method: "POST",
      mode: "cors",
      headers: { "Content-Type": "application/json", },
      body: JSON.stringify({ tournament_id: urlTournamentId, tournament_name: newTournamentName, opening: openingDate }),
    })
      .then((response) => response.text())
      .then((data) => readTournament(setTournamentData))
  }
}

//削除
const tournamentDelete = (tournamentId) => {
  fetch(backendUrl + "/tournament/tournament_edit", {
    method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }, body: JSON.stringify({ tournament_id: tournamentId }),
  })
    .then((response) => response.text())
    .then((data) => { if (data === "OK") console.log("削除しました") })

  // console.log(tournamentId + "削除しました")
}


const loadVenue = (setVenueArray) => {
  fetch(backendUrl + "/venue/venue_call", {
    method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
  })
    .then((response) => response.json())
    .then((data) => { setVenueArray(data) })

  // console.log(tournamentId + "削除しました")
}

const editVenue = async (venueId, venueName) => {
  const Fetch = fetch(backendUrl + "/venue/venue_edit", {
    method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
    , body: JSON.stringify({ venue_id: venueId, venue_name: venueName }),
  })
  const FetchReturn = (await Fetch).text()
}

const deleteVenue = async (venueId) => {
  const Fetch = fetch(backendUrl + "/venue/venue_edit", {
    method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
    , body: JSON.stringify({ venue_id: venueId }),
  })
  const FetchReturn = (await Fetch).text()
}



export const RegisterVenue = () => {

  //プルダウンの選択肢の値
  const initialYear = 2020
  const endYear = 2040
  const initialMonth = 1
  const endMonth = 12
  const initialday = 1
  const endDay = 31

  //プルダウンのリスト作成
  let yearArray = []
  for (let i = initialYear; i <= endYear; i++) { yearArray = [...yearArray, { year: i }] }

  let monthArray = []
  for (let i = initialMonth; i <= endMonth; i++) { monthArray = [...monthArray, { month: i }] }

  let dayArray = []
  for (let i = initialday; i <= endDay; i++) { dayArray = [...dayArray, { day: i }] }

  //大会の日付を入力するときのステイト1:年, 2:月, 3:日
  const [nowOpeningDate, setNowOpeningDate] = useState([0, 0, 0])

  //大会の日付を編集するときのステイト1:年, 2:月, 3:日
  const [editOpeningDate, setEditOpeningDate] = useState([0, 0, 0])

  //会場名を入力する時のステイト
  const [nowVenueName, setNowVenueName] = useState("")

  //会場のリストを管理するステイト
  const [venueArray, setVenueArray] = useState([])

  //編集中の大会名を管理するステイト
  const [editingVenueName, setEditingVenueName] = useState("")

  //編集するか削除するかのチェックボックスを管理するステイト,trueなら編集,falseなら削除
  const [EorDcheckBox, setEorDcheckBox] = useState(true)


  //文字分割のための箱を用意
  let dateArray = { "year": "", "month": "", "day": "" }

  //適当な初期値
  let [TournamentData, setTournamentData] = useState([
    { "tournament_name": '第31回春大会', "tournament_id": '55', "opening": "2022-03-01" },
    { "tournament_name": '第9回松山大会', "tournament_id": '56', "opening": "2022-02-01" },
    { "tournament_name": '第5回伊予大会', "tournament_id": '57', "opening": "2022-01-01" },
    { "tournament_name": '第22回秋大会', "tournament_id": '58', "opening": "2021-08-01" }
  ])

  //ページ遷移用
  const navigate = useNavigate()
  const PageTransition = (url) => {
    navigate(url)
  }


  useEffect(() => {
    readTournament(setTournamentData);
  }, []);

  useEffect(() => {
    loadVenue(setVenueArray)
  }, [])

  return (
    <div>
      <TitleBar
        TitleText={"大会入力選択画面"}
        PageTransition={PageTransition}
        valueUrl={-1}
      />

      <OptionButton />


      <div class="headline">会場追加</div>
      <div class="whole1">
        会場名
        <input type="text" onChange={(e) => { setNowVenueName(e.target.value) }} />
        <br />

        {/* 追加ボタン */}
        {isEnpty([nowVenueName]) &&
          <button
            class="btn_In_to"
            onClick={() => { }}>追加
          </button>
        }

        {!isEnpty([nowVenueName]) &&
          <button
            class="btn_In_to"
            onClick={() => {
              addVenue(venueArray, setVenueArray, nowVenueName)
            }}>追加
          </button>
        }

      </div>

      <div class="headline">会場編集</div>
      <div class="whole">
        <div className="tournamentList">
          <div className="tournaments">

            {venueArray.map((Venue, ind) => {
              if (Venue.length !== 0) {
                return (
                  <>
                    <EditVenuePopup
                      sendClassName="btn_In_to1"
                      Venue={Venue}
                      ind={ind}
                      editVenue={editVenue}
                      venueArray={venueArray}
                      setVenueArray={setVenueArray}
                      editingVenueName={editingVenueName}
                      setEditingVenueName={setEditingVenueName}
                      EorDcheckBox={EorDcheckBox}
                      setEorDcheckBox={setEorDcheckBox}
                      deleteVenue={deleteVenue}
                      loadVenue={loadVenue}
                      isDuplicate={isDuplicate}
                    />

                  </>
                )
              }
            })}

            {/* {TournamentData.map((Tournament, ind) => {

              if (Tournament.tournament_name !== null) {
                //文字分割
                dateArray = dateSplit(Tournament.opening)

                return (
                  <div className="tournament">
                    <div className="days">
                      <span>{dateArray.year}年{dateArray.month}月{dateArray.day}日</span>
                    </div>
                    <div className="tournamentName">
                      {isDeleteMode &&
                        <>
                          <EditVenuePopup
                            sendClassName="btn_In_to1"
                            Tournament={Tournament}
                            ind={ind}
                            editTournament={editTournament}
                            editOpeningDate={editOpeningDate}
                            setEditOpeningDate={setEditOpeningDate}
                            yearArray={yearArray}
                            monthArray={monthArray}
                            dayArray={dayArray}
