import React, { useRef, useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
const { Member } = require("../../../../DB/Member");
const { PositionDB } = require("../../../../DB/PositionDB")

const tableStyle = {
    border: '1px solid black',
    borderCollapse: 'collapse',
    textAlign: 'center',
    width: '70%'
}

const tdStyle = {
    border: '1px solid #85C1E9',
    background: 'white',
    padding: '5px'
};

const thStyle = {
    border: '1px solid #3498DB',
    background: '#3498DB',
    color: 'white',
    padding: '2px'
};


//選手を読み込む
const loadRegisteredMember = (urlTournamentId, urlSchoolId, setRegisteredMember) => {
    fetch("http://localhost:5000/member/tournament_member_call", {
        method: "POST",
        mode: "cors",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({ tournament_id: urlTournamentId, school_id: urlSchoolId }),
    })
        .then((response) => response.json())
        .then((data) => {
            console.log(data)
            setRegisteredMember(data)
        })
}



//自作プルダウン
const makePulldown = (pulldownId, ArrayList, idText, nowSelected, setNowSelected) => {
    return (
        <>
            <select id="tekitouni"
                onChange={(e) => {
                    //ステイトが変化すると再描画させるための文、これがないと再描画されない
                    //なお、消すと再描画はされないが内部は変化する
                    nowSelected = nowSelected.slice(0, nowSelected.length);

                    nowSelected[pulldownId] = e.target.value
                    setNowSelected(nowSelected)
                    console.log(nowSelected)
                }}>
                {ArrayList.map((component, ind) => (
                    <option value={ind}>{component[idText]}</option>
                ))
                }
            </select>
        </>

    )
}


const StartingMember = () => {

    //プルダウンのための初期値作成
    let initialPulldown = []
    for (let i = 0; i < 18; i++) {
        initialPulldown = [...initialPulldown, 0]
    }

    //今選択しているものの内容を監視
    const [nowSelected, setNowSelected] = useState(initialPulldown) //チーム1
    const [nowSelected2, setNowSelected2] = useState(initialPulldown) //チーム2

    //map関数を使うための空配列作成
    let enptyArray = []
    for (let i = 0; i < 9; i++) {
        enptyArray = [...enptyArray, 0]
    }

    //urlから値を読み出す
    const [searchParams] = useSearchParams();
    const urlTournamentId = searchParams.get("urlTournamentId")
    const urlTournamentName = searchParams.get("urlTournamentName")
    const urlSchoolId = searchParams.get("urlSchoolId")
    const urlSchoolName = searchParams.get("urlSchoolName")
    const urlSchoolId2 = searchParams.get("urlSchoolId2")
    const urlSchoolName2 = searchParams.get("urlSchoolName2")
    const urlGameId = searchParams.get("urlGameId")

    //ステイト定義
    const [registeredMember1, setRegisteredMember1] = useState([])
    const [registeredMember2, setRegisteredMember2] = useState([])


    useEffect(() => {
        loadRegisteredMember(urlTournamentId, urlSchoolId, setRegisteredMember1)
        loadRegisteredMember(urlTournamentId, urlSchoolId2, setRegisteredMember2)
    }, [])

    return (
        <div align='center'>
            {console.log(registeredMember1[0].BA)}
            <h2>スタメン選択画面</h2>
            <h3>編集中：{urlTournamentName}</h3>
            <div id="error"></div>
            <table style={tableStyle}><tr><th>{urlSchoolName}</th></tr></table>
            <table style={tableStyle}>
                <tbody>
                    <tr>
                        <th style={thStyle} rowspan="2">打順</th>
                        <th style={thStyle} rowspan="2">位置</th>
                        <th style={thStyle}>ふりがな</th>
                        <th style={thStyle} rowspan="2">背番号</th>
                        <th style={thStyle} rowspan="2">学年</th>
                    </tr>
                    <tr><th style={thStyle}>選手氏名</th></tr>

                    {enptyArray.map((component, ind) => (
                        <>
                            <tr>
                                <td style={tdStyle} rowspan="2">{ind + 1}</td>
                                <td style={tdStyle} rowspan="2">

                                    {makePulldown(ind, PositionDB, "kata", nowSelected, setNowSelected)}
                                </td>
                                <td style={tdStyle}><div id="player_name_hira1">{Member[nowSelected[ind + 9]].player_name_hira}</div></td>
                                <td style={tdStyle} rowspan="2"><div id="uniform_number1">{Member[nowSelected[ind + 9]].uniform_number}</div></td>
                                <td style={tdStyle} rowspan="2"><div id="grade1">{Member[nowSelected[ind + 9]].grade}</div></td>
                            </tr>
                            <tr>
                                <td style={tdStyle}>
                                    {makePulldown(ind + 9, registeredMember1, "player_name_kanji", nowSelected, setNowSelected)}
                                </td>
                            </tr>
                        </>
                    ))
                    }

                </tbody>
            </table><br />

            <table style={tableStyle}><tr><th>{urlSchoolName2}</th></tr></table>
            <table style={tableStyle}>
                <tbody>
                    <tr>
                        <th style={thStyle} rowspan="2">打順</th>
                        <th style={thStyle} rowspan="2">位置</th>
                        <th style={thStyle}>ふりがな</th>
                        <th style={thStyle} rowspan="2">背番号</th>
                        <th style={thStyle} rowspan="2">学年</th>
                    </tr>
                    <tr><th style={thStyle}>選手氏名</th></tr>

                    {enptyArray.map((component, ind) => (
                        <>
                            <tr>
                                <td style={tdStyle} rowspan="2">{ind + 1}</td>
                                <td style={tdStyle} rowspan="2">

                                    {makePulldown(ind, PositionDB, "kata", nowSelected2, setNowSelected2)}
                                </td>
                                <td style={tdStyle}><div id="player_name_hira1">{Member[nowSelected2[ind + 9]].player_name_hira}</div></td>
                                <td style={tdStyle} rowspan="2"><div id="uniform_number1">{Member[nowSelected2[ind + 9]].uniform_number}</div></td>
                                <td style={tdStyle} rowspan="2"><div id="grade1">{Member[nowSelected2[ind + 9]].grade}</div></td>
                            </tr>
                            <tr>
                                <td style={tdStyle}>
                                    {makePulldown(ind + 9, Member, "player_name_kanji", nowSelected2, setNowSelected2)}
                                </td>
                            </tr>
                        </>
                    ))
                    }

                </tbody>
