// import { OptionFunc } from '../../../../../../'
// import { OptionFunc } from '../../../../../Functions/OptionFunc/OptionFunc'
import { useState } from 'react';
import { useEffect } from 'react'
import { useSearchParams, useNavigate } from 'react-router-dom'

import "./home.css"

//バックエンドのurlを取得
const backendUrl = require("../../../../../DB/communication").backendUrl;

const goToPage = () => {
    console.log("移動します")
}


//データベースから大会情報を読み出す
const loadGame = async (urlTournamentId) => {
    await fetch(backendUrl + "/game/game_call", {
        method: "POST", mode: "cors", headers: { "Content-Type": "application/json", },
        body: JSON.stringify({ tournament_id: urlTournamentId }),
    })
        .then((response) => response.json())
        .then((data) => {
            console.log(data)
        })
}

//試合中の試合の情報を参照する
const RefDuringGame = async () => {
    fetch(backendUrl + "/game/ref_during_game", {
        method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
    })
        .then((response) => response.json())
        .then((data) => {
            console.log(data)
        })
}

//

const GameList = () => {

    //ページ遷移用
    const navigate = useNavigate()
    const PageTransition = (url) => {
        navigate(url)
    }

    const [searchParams] = useSearchParams();

    const urlTournamentId = searchParams.get("urlTournamentId")
    const urlTournamentName = searchParams.get("urlTournamentName")
    const urlTournamentOpening = searchParams.get("urlTournamentOpening")


    //大会情報を管理するステイト
    const [rgsTournament, setRgsTournament] = useState([])



    //オプションメニューのための配列作るよ
    const optionArray = [{ name: "過去データ", url: "/PastDataHome" }]


    useEffect(() => {
        const handleUseEffect = async () => {
            await loadGame(urlTournamentId)
            await RefDuringGame()
        }

        //useEffectの内部を実行
        handleUseEffect()
    }, [])


    return (
        <>
            {/* <OptionFunc
                menuName={"メニュー"}
                optionArray={optionArray}
                PageTransition={PageTransition}
            ></OptionFunc> */}

            <div class="whole">
                <div class="date">
                    {/* <!-- <input type="date" name="today" id="today"> --> */}
                    2023年10月30日(日)urlTournamentOpening
                </div>
                <div class='targetModules'>
                    <div class='gameName'>春季中国地区高等学校野球島根県大会　東部地区</div>
                    <div class="displayGames" onClick={goToPage}>
                        <div class='gameDetaile'>
                            <div class='gameRound'>3回戦</div>
                            <div class='gameCard'>
                                <div class='firstAttackTeam, teamName'>平田</div>
                                <div class='gameScore'>
                                    <div class='firstAttackTeamScore'>1</div>
                                    <div class='gameState'>5回裏</div>
                                    <div class='secondAttackTeamScore'>3</div>
                                </div>
                                <div class='secondAttackTeam, teamName'>出雲</div>
                            </div>
                            <div class="gamePlace">浜山公園野球場</div>
                        </div>
                        <p></p>

                    </div>
                </div>
            </div>
        </>

    );
}

export default GameList;
