import "./CSS/outCount.css"

const outZero = (setOutCountState) => {
    return (
        <>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(1)}></i>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(2)}></i>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(3)}></i>
        </>

    )
}

const outOne = (setOutCountState) => {
    return (
        <>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(0)}></i>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(2)}></i>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(3)}></i>
        </>

    )
}

const outTwo = (setOutCountState) => {
    return (
        <>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(1)}></i>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(0)}></i>
            <i className="fa-solid fa-circle outCountOff" onClick={() => setOutCountState(3)}></i>
        </>

    )
}

const outThree = (setOutCountState) => {
    return (
        <>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(1)}></i>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(2)}></i>
            <i className="fa-solid fa-circle outCountOn" onClick={() => setOutCountState(0)}></i>
        </>

    )
}


export const outCount = (outCountState, setOutCountState) => {

    return (
        <div className="outCount">
            {outCountState === 0 && outZero(setOutCountState)}
            {outCountState === 1 && outOne(setOutCountState)}
            {outCountState === 2 && outTwo(setOutCountState)}
            {outCountState === 3 && outThree(setOutCountState)}
        </div>
    )

}

