// import { OptionFunc } from '../../../../../../'
// import { OptionFunc } from '../../../../../Functions/OptionFunc/OptionFunc'
import { useState } from 'react';
import { useEffect } from 'react'
import { useSearchParams, useNavigate } from 'react-router-dom'

import "./home.css"

//バックエンドのurlを取得
const backendUrl = require("../../../../../DB/communication").backendUrl;

const goToPage = () => {
    console.log("移動します")
}



//データベースから大会情報を読み出す
const loadGame = async (urlTournamentId, setGameList) => {

    //データベースから大会情報を読み出すフェッチ文
    const funcGL = await fetch(backendUrl + "/game/game_call", {
        method: "POST", mode: "cors", headers: { "Content-Type": "application/json", },
        body: JSON.stringify({ tournament_id: urlTournamentId }),
    })
    const receivedGL = await funcGL.json()
    await setGameList(receivedGL)

    //試合中の試合の情報を参照するフェッチ文
    const funcDG = await fetch(backendUrl + "/game/ref_during_game", {
        method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
    })
    const receivedDG = await funcDG.json()
    console.log(receivedDG)

    receivedGL.map((u) => {
        const resortArray1 = []
        if (receivedDG.some((v) => v.game_id === u.game_id)) {

        }
        else {
            resortArray = [...resortArray, u]
        }
    })

}

//試合中の試合の情報を参照する
const RefDuringGame = async () => {
    fetch(backendUrl + "/game/ref_during_game", {
        method: "POST", mode: "cors", headers: { "Content-Type": "application/json", }
    })
        .then((response) => response.json())
        .then((data) => {
            console.log(data)
        })
}

//

const GameList = () => {

    //ページ遷移用
    const navigate = useNavigate()
    const PageTransition = (url) => {
        navigate(url)
    }

    const [searchParams] = useSearchParams();

    const urlTournamentId = searchParams.get("urlTournamentId")
    const urlTournamentName = searchParams.get("urlTournamentName")
    const urlTournamentOpening = searchParams.get("urlTournamentOpening").split("-")

    //大会情報を管理するステイト
    const [rgsTournament, setRgsTournament] = useState([])

    const initialGL = [{
        first_rear_1: "",
        first_rear_2: "",
        game_id: 0,
        game_ymd: "",
        match_num: "",
        match_results: null,
        school_id_1: 0,
        school_id_2: 0,
        school_name_1: "",
        school_name_2: "",
        tournament_id: 0,
        venue_id: 0,
        venue_name: ""
    }]

    //試合リストを管理するステイト
    const [gameList, setGameList] = useState(initialGL)

    //オプションメニューのための配列作るよ
    const optionArray = [{ name: "過去データ", url: "/PastDataHome" }]


    useEffect(() => {
        const handleUseEffect = async () => {
            await loadGame(urlTournamentId, setGameList)
            await RefDuringGame()
        }

        //useEffectの内部を実行
        handleUseEffect()
    }, [])


    return (
        <>
            {/* <OptionFunc
                menuName={"メニュー"}
                optionArray={optionArray}
                PageTransition={PageTransition}
            ></OptionFunc> */}


            <div class="whole">
                <div class="date">
                    {/* <!-- <input type="date" name="today" id="today"> --> */}
                    2023年10月30日(日)
                    {console.log(gameList)}
                </div>
                <div class='targetModules'>
                    <div class='gameName'>春季中国地区高等学校野球島根県大会　東部地区</div>
                    <div class="displayGames" onClick={goToPage}>
                        <div class='gameDetaile'>
                            <div class='gameRound'>3回戦</div>
                            <div class='gameCard'>
                                <div class='firstAttackTeam, teamName'>平田</div>
                                <div class='gameScore'>
                                    <div class='firstAttackTeamScore'>1</div>
                                    <div class='gameState'>5回裏</div>
                                    <div class='secondAttackTeamScore'>3</div>
                                </div>
                                <div class='secondAttackTeam, teamName'>出雲</div>
                            </div>
                            <div class="gamePlace">浜山公園野球場</div>
                        </div>
                        <p></p>

                    </div>
                </div>
            </div>
        </>

    );
}

export default GameList;
